CREATE DATABASE DB_BANDIENTHOAI_DONGHO;
USE DB_BANDIENTHOAI_DONGHO;
#Bang loai san pham
CREATE TABLE LOAI_SAN_PHAM
(
ID_LSP INT NOT NULL AUTO_INCREMENT,
TEN_LSP VARCHAR (500) NOT NULL,
DONG_SP VARCHAR (100) NOT NULL,
PHAN_KHUC VARCHAR (100) NOT NULL,
PRIMARY KEY (ID_LSP)
)ENGINE = InnoDB DEFAULT CHARSET=utf8mb4;
#Bang nha cung cap
CREATE TABLE NHA_CUNG_CAP
(
ID_NCC INT NOT NULL AUTO_INCREMENT,
TEN_NHA_CUNG_CAP VARCHAR (5000) NOT NULL,
DIA_CHI VARCHAR (1000) NOT NULL,
EMAIL VARCHAR (1000) NOT NULL,
SO_DIEN_THOAI INT NOT NULL,
PRIMARY KEY (ID_NCC)
)ENGINE = InnoDB DEFAULT CHARSET=utf8mb4;
#bang nha san xuat
CREATE TABLE NHA_SAN_XUAT
(
ID_NSX INT NOT NULL AUTO_INCREMENT,
LOGO VARCHAR(200) NULL,
TEN_NHA_SAN_XUAT VARCHAR (5000) NOT NULL,
DIA_CHI VARCHAR (1000) NOT NULL,
EMAIL VARCHAR (1000) NOT NULL,
SO_DIEN_THOAI INT NOT NULL,
PRIMARY KEY (ID_NSX)
)ENGINE = InnoDB DEFAULT CHARSET=utf8mb4;
#bang san pham
CREATE TABLE SAN_PHAM
(
ID_SP INT NOT NULL AUTO_INCREMENT,
TEN_SAN_PHAM VARCHAR (500) NOT NULL,
DEMO INT NOT NULL,
GIA_NIEM_YET DOUBLE NOT NULL,
SALE VARCHAR (10) NOT NULL,
GIA_BAN  DOUBLE NOT NULL,
NGAY_CAP_NHAT DATETIME NULL,
NAM_SAN_XUAT YEAR NULL,
XUAT_XU VARCHAR (50) NOT NULL,
NOI_SAN_XUAT VARCHAR (200) NOT NULL,
THONG_SO_KY_THUAT TEXT NULL,
GIOI_THIEU_CHUNG TEXT NULL,
TIEU_DE_1 TEXT NULL,
MO_TA_CT1 TEXT NULL,
TIEU_DE_2 TEXT NULL,
MO_TA_CT2 TEXT NULL,
TIEU_DE_3 TEXT NULL,
MO_TA_CT3 TEXT NULL,
HINH_ANH_1 VARCHAR(200) NULL,
SO_LUONG INT NOT NULL,
ID_NCC INT NOT NULL,
ID_NSX INT NOT NULL,
ID_LSP INT NOT NULL,
TINH_TRANG VARCHAR(100) NOT NULL,
PRIMARY KEY (ID_SP),
FOREIGN KEY (ID_NCC) REFERENCES NHA_CUNG_CAP(ID_NCC),
FOREIGN KEY (ID_NSX) REFERENCES NHA_SAN_XUAT(ID_NSX),
FOREIGN KEY (ID_LSP) REFERENCES LOAI_SAN_PHAM(ID_LSP)
)ENGINE = InnoDB DEFAULT CHARSET=utf8mb4;
#bang phieu nhap
CREATE TABLE PHIEU_NHAP
(
ID_PN INT NOT NULL AUTO_INCREMENT,
ID_NCC INT NOT NULL,
NGAY_NHAP DATETIME NOT NULL,
DON_GIA_NHAP DECIMAL (20) NOT NULL,
SO_LUONG_NHAP INT NOT NULL,
TINH_TRANG VARCHAR(100) NOT NULL,
PRIMARY KEY (ID_PN),
FOREIGN KEY (ID_NCC) REFERENCES NHA_CUNG_CAP(ID_NCC)
)ENGINE = InnoDB DEFAULT CHARSET=utf8mb4;
#bang loai thanh vien
CREATE TABLE LOAI_THANH_VIEN
(
ID_LTV INT NOT NULL AUTO_INCREMENT,
TEN_LOAI_THANH_VIEN VARCHAR(200) NOT NULL,
UU_DAI VARCHAR(20) NOT NULL,
PRIMARY KEY (ID_LTV)
)ENGINE = InnoDB DEFAULT CHARSET=utf8mb4;
#bang thanh vien
CREATE TABLE THANH_VIEN
(
ID_TV INT NOT NULL AUTO_INCREMENT,
TEN_TAI_KHOAN VARCHAR (300) NOT NULL,
MAT_KHAU VARCHAR (20) NOT NULL,
TEN_THANH_VIEN VARCHAR(200) NOT NULL,
DIA_CHI VARCHAR(200) NOT NULL,
EMAIL VARCHAR(200) NOT NULL,
ID_LTV INT NOT NULL,
PRIMARY KEY (ID_TV),
FOREIGN KEY (ID_LTV) REFERENCES LOAI_THANH_VIEN(ID_LTV)
)ENGINE = InnoDB DEFAULT CHARSET=utf8mb4;
#bang khach hang
CREATE TABLE KHACH_HANG
(
ID_KH INT NOT NULL AUTO_INCREMENT,
TEN_KHACH_HANG VARCHAR(200) NOT NULL,
DIA_CHI VARCHAR (200) NOT NULL,
EMAIL VARCHAR (200) NOT NULL,
SO_DIEN_THOAI INT NOT NULL,
ID_TV INT,
PRIMARY KEY (ID_KH),
FOREIGN KEY (ID_TV) REFERENCES THANH_VIEN(ID_TV)
)ENGINE = InnoDB DEFAULT CHARSET=utf8mb4;
#bang don dat hang
CREATE TABLE DON_DAT_HANG
(
ID_DDH INT NOT NULL AUTO_INCREMENT,
ID_KH INT NOT NULL,
NGAY_DAT DATETIME NOT NULL,
TONG_TIEN DOUBLE NOT NULL,
TRA_TIEN INT NOT NULL,
TINH_TRANG INT NULL,
PRIMARY KEY (ID_DDH),
FOREIGN KEY (ID_KH) REFERENCES KHACH_HANG(ID_KH)
)ENGINE = InnoDB DEFAULT CHARSET=utf8mb4;
#chi tiết đon dat hang
CREATE TABLE CHITIET_DDH
(
ID_CTDDH INT NOT NULL AUTO_INCREMENT,
ID_DDH INT NOT NULL,
ID_SP INT NOT NULL,
SO_LUONG INT NOT NULL,
GIA_BAN DOUBLE NOT NULL,
PRIMARY KEY (ID_CTDDH),
FOREIGN KEY (ID_DDH) REFERENCES DON_DAT_HANG(ID_DDH),
FOREIGN KEY (ID_SP) REFERENCES SAN_PHAM(ID_SP)
)ENGINE = InnoDB DEFAULT CHARSET=utf8mb4;

#bang slide hinh anh
CREATE TABLE SLIDE_HINHANH
(
ID_HINHANH INT NOT NULL AUTO_INCREMENT,
TEN_HINH_ANH VARCHAR (200) NOT NULL,
HINH_ANH VARCHAR(200) NOT NULL,
PRIMARY KEY (ID_HINHANH)
)ENGINE = InnoDB DEFAULT CHARSET=utf8mb4;
#bang vai tro
CREATE TABLE VAI_TRO
(
ID_VT INT NOT NULL,
TEN_LOAI_THANH_VIEN VARCHAR (200) NOT NULL,
VAI_TRO VARCHAR (100) NOT NULL,
ID_LTV INT NOT NULL,
PRIMARY KEY (ID_VT),
FOREIGN KEY (ID_LTV) REFERENCES LOAI_THANH_VIEN(ID_LTV)
)ENGINE = InnoDB DEFAULT CHARSET=utf8mb4;
#bang danh muc#
#drop table danh_muc;
CREATE TABLE DANH_MUC
(
ID_DM INT NOT NULL,
TEN_DANH_MUC VARCHAR (200) NOT NULL,
ID_NSX INT,
ID_NCC INT,
PRIMARY KEY (ID_DM),
FOREIGN KEY (ID_NSX) REFERENCES NHA_SAN_XUAT(ID_NSX),
FOREIGN KEY (ID_NCC) REFERENCES NHA_CUNG_CAP(ID_NCC)
)ENGINE = InnoDB DEFAULT CHARSET=utf8mb4;

